INSERT INTO "dublincoredocument" VALUES ('39322', null, 'CiteSeerX', null, 'Hisham A. Al-Mharmah', '1998', 'In this paper we compare the average performance of one class of low-discrepancy quasi-Monte Carlo sequences for global optimization. Weiner measure is assumed as the probability prior on all optimized functions. We show how to construct van der Corput sequences and we prove their consistency. Numerical experimentation shows that the van der Corput sequence in base 2 has a better average performance.', null, null, 'http://citeseerx.ist.psu.edu/viewdoc/summary?doi=10.1.1.1.1521', 'en', '', '10.1.1.22.679', 'Metadata may be used without restrictions as long as the oai identifier remains attached to it.', 'http://www.informs-cs.org/wsc98papers/083.PDF', null, 'Average performance of quasi Monte Carlo methods for global optimization', '3');
INSERT INTO "dublincoredocument" VALUES ('39323', null, 'CiteSeerX', null, 'Alex F. Sisti', '1998', 'This paper addresses the opportunity to put into place a virtual consortium for modeling and simulation. While periodic conferences such as the Winter Simulation Conference are tremendously vital to the continued growth of modeling and simulation research, they do not offer the day-to-day technical exchange that can now be made possible with matured collaborative technologies.', null, null, 'http://citeseerx.ist.psu.edu/viewdoc/summary?doi=10.1.1.1.1522', 'en', '', null, 'Metadata may be used without restrictions as long as the oai identifier remains attached to it.', 'http://www.informs-cs.org/wsc98papers/228.PDF', null, 'The Virtual Ms Lyceum: A Consortium For Modeling And Simulation Technology', '3');
